import { useEffect } from 'react';
import { useLocation } from 'wouter';
import { useToast } from '@/hooks/use-toast';

export function useKeyboardShortcuts() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      // Check if user is typing in an input field
      const isInputFocused = document.activeElement?.tagName === 'INPUT' || 
                            document.activeElement?.tagName === 'TEXTAREA';

      if (isInputFocused && !e.ctrlKey && !e.metaKey) return;

      // Ctrl/Cmd + K - Quick search
      if ((e.ctrlKey || e.metaKey) && e.key === 'k') {
        e.preventDefault();
        toast({
          title: "Quick Search",
          description: "Quick search activated (Feature coming soon)",
        });
      }

      // Ctrl/Cmd + S - Send transaction
      if ((e.ctrlKey || e.metaKey) && e.key === 's') {
        e.preventDefault();
        setLocation('/send');
      }

      // Ctrl/Cmd + D - Dashboard
      if ((e.ctrlKey || e.metaKey) && e.key === 'd') {
        e.preventDefault();
        setLocation('/dashboard');
      }

      // Ctrl/Cmd + H - History
      if ((e.ctrlKey || e.metaKey) && e.key === 'h') {
        e.preventDefault();
        setLocation('/history');
      }

      // Ctrl/Cmd + P - Pricing
      if ((e.ctrlKey || e.metaKey) && e.key === 'p' && !e.shiftKey) {
        e.preventDefault();
        setLocation('/pricing');
      }

      // Ctrl/Cmd + , - Settings
      if ((e.ctrlKey || e.metaKey) && e.key === ',') {
        e.preventDefault();
        setLocation('/settings');
      }

      // Ctrl/Cmd + Shift + L - Logout
      if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.key === 'L') {
        e.preventDefault();
        window.location.href = '/api/logout';
      }

      // ? - Show shortcuts help
      if (e.key === '?' && !isInputFocused) {
        e.preventDefault();
        showShortcutsHelp();
      }

      // Escape - Close modals
      if (e.key === 'Escape') {
        // This will be handled by individual modal components
        document.dispatchEvent(new CustomEvent('closeModal'));
      }
    };

    const showShortcutsHelp = () => {
      toast({
        title: "Keyboard Shortcuts",
        description: `â€¢ Ctrl/Cmd + S - Send Transaction
â€¢ Ctrl/Cmd + D - Dashboard
â€¢ Ctrl/Cmd + H - History
â€¢ Ctrl/Cmd + P - Pricing
â€¢ Ctrl/Cmd + , - Settings
â€¢ Ctrl/Cmd + K - Quick Search
â€¢ ? - Show this help`,
      });
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [setLocation, toast]);
}

client/src/components/ExitIntentPopup.ts.content:
import { useState, useEffect } from 'react';
import { X, Gift, Clock, AlertTriangle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { motion, AnimatePresence } from 'framer-motion';

export default function ExitIntentPopup() {
  const [showPopup, setShowPopup] = useState(false);
  const [hasShown, setHasShown] = useState(false);

  useEffect(() => {
    const handleMouseLeave = (e: MouseEvent) => {
      // Detect when mouse leaves the viewport from the top
      if (e.clientY <= 0 && !hasShown) {
        const lastShown = localStorage.getItem('exitIntentLastShown');
        const now = Date.now();

        // Show only once per day
        if (!lastShown || now - parseInt(lastShown) > 86400000) {
          setShowPopup(true);
          setHasShown(true);
          localStorage.setItem('exitIntentLastShown', now.toString());
        }
      }
    };

    document.addEventListener('mouseleave', handleMouseLeave);
    return () => document.removeEventListener('mouseleave', handleMouseLeave);
  }, [hasShown]);

  const handleClaim = () => {
    // Apply discount code
    localStorage.setItem('appliedDiscount', 'EXIT30');
    setShowPopup(false);
    window.location.href = '/pricing';
  };

  const handleClose = () => {
    setShowPopup(false);
  };

  if (!showPopup) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 z-[200] bg-black/80 backdrop-blur-sm flex items-center justify-center p-4"
        onClick={handleClose}
      >
        <motion.div
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.8, opacity: 0 }}
          className="max-w-lg w-full bg-gradient-to-br from-purple-900 to-black border border-purple-500/30 rounded-xl overflow-hidden"
          onClick={(e) => e.stopPropagation()}
        >
          {/* Header */}
          <div className="relative p-6 pb-0">
            <button
              onClick={handleClose}
              className="absolute top-4 right-4 text-gray-400 hover:text-white"
            >
              <X className="w-6 h-6" />
            </button>

            <div className="text-center">
              <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-full mb-4">
                <Gift className="w-10 h-10 text-white" />
              </div>

              <h2 className="text-3xl font-bold text-white mb-2">
                Wait! Don't Leave Yet!
              </h2>

              <div className="flex items-center justify-center gap-2 mb-4">
                <AlertTriangle className="w-5 h-5 text-yellow-400" />
                <p className="text-yellow-400 font-semibold">
                  Exclusive Offer - Valid for 10 minutes only!
                </p>
              </div>
            </div>
          </div>

          {/* Content */}
          <div className="p-6 space-y-4">
            <div className="bg-black/50 rounded-lg p-4 border border-purple-500/30">
              <div className="text-center mb-4">
                <span className="text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-400">
                  30% OFF
                </span>
                <p className="text-gray-300 mt-2">Your First Month Subscription</p>
              </div>

              <div className="space-y-3">
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-400 rounded-full"></div>
                  <span className="text-gray-300">Valid for all subscription plans</span>
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-400 rounded-full"></div>
                  <span className="text-gray-300">Instant activation upon payment</span>
                </div>
                <div className="flex items-center gap-3">
                  <div className="w-2 h-2 bg-green-400 rounded-full"></div>
                  <span className="text-gray-300">No hidden fees or commitments</span>
                </div>
              </div>
            </div>

            <div className="bg-red-900/20 border border-red-500/30 rounded-lg p-3">
              <div className="flex items-center gap-2">
                <Clock className="w-5 h-5 text-red-400" />
                <div className="flex-1">
                  <p className="text-red-400 font-semibold">Limited Time Offer</p>
                  <p className="text-xs text-gray-400">This offer expires when you leave the page</p>
                </div>
              </div>
            </div>

            <div className="flex gap-3">
              <Button
                onClick={handleClaim}
                className="flex-1 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white"
                size="lg"
              >
                Claim 30% Discount
              </Button>
              <Button
                onClick={handleClose}
                variant="outline"
                className="border-gray-600 text-gray-300 hover:bg-gray-800"
                size="lg"
              >
                No Thanks
              </Button>
            </div>

            <p className="text-center text-xs text-gray-500">
              * Discount automatically applied at checkout. One-time use only.
            </p>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}

client/src/components/AgeVerification.ts.content:
import { useState, useEffect } from 'react';
import { Shield, AlertTriangle, Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { motion, AnimatePresence } from 'framer-motion';

export default function AgeVerification() {
  const [showVerification, setShowVerification] = useState(false);
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    const verified = localStorage.getItem('ageVerified');
    if (!verified) {
      setTimeout(() => setShowVerification(true), 500);
    } else {
      setIsVerified(true);
    }
  }, []);

  const handleVerify = (isOfAge: boolean) => {
    if (isOfAge) {
      localStorage.setItem('ageVerified', 'true');
      localStorage.setItem('ageVerifiedDate', new Date().toISOString());
      setIsVerified(true);
      setShowVerification(false);
    } else {
      // Redirect to appropriate page
      window.location.href = 'https://www.google.com';
    }
  };

  if (!showVerification || isVerified) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 z-[100] bg-black/95 backdrop-blur-xl flex items-center justify-center p-4"
      >
        <motion.div
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          className="max-w-md w-full bg-gradient-to-br from-purple-900/50 to-black/50 border border-purple-500/30 rounded-xl p-8"
        >
          <div className="flex flex-col items-center text-center space-y-6">
            <div className="relative">
              <Shield className="w-20 h-20 text-purple-400" />
              <AlertTriangle className="w-8 h-8 text-yellow-400 absolute -bottom-2 -right-2" />
            </div>

            <div>
              <h2 className="text-2xl font-bold text-white mb-2">Age Verification Required</h2>
              <p className="text-gray-400">
                You must be 18 years or older to access this cryptocurrency platform.
                This service involves financial transactions and is restricted to adults only.
              </p>
            </div>

            <div className="w-full space-y-3">
              <Button
                onClick={() => handleVerify(true)}
                className="w-full bg-purple-600 hover:bg-purple-700 text-white"
                size="lg"
              >
                <Calendar className="w-5 h-5 mr-2" />
                I am 18 or older
              </Button>

              <Button
                onClick={() => handleVerify(false)}
                variant="outline"
                className="w-full border-gray-600 text-gray-300 hover:bg-gray-800"
                size="lg"
              >
                I am under 18
              </Button>
            </div>

            <p className="text-xs text-gray-500 mt-4">
              By proceeding, you confirm that you meet the age requirement and agree to our Terms of Service.
              We use cookies to remember your age verification status.
            </p>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
}

