import React, { lazy, Suspense } from "react";
import { Route, Switch, useLocation, Redirect } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider, useAuth } from "./hooks/use-auth";
import { useKeyboardShortcuts } from './hooks/useKeyboardShortcuts';
import { useEffect } from 'react';
import { lazyLoad, PageLoader } from './utils/lazyLoad';

// Critical components loaded immediately
import Login from "./pages/login";
import Homepage from "./pages/homepage";
import { WhatsAppButton } from './components/WhatsAppButton';

// Lazy load heavy components
const Dashboard = lazyLoad(() => import("./pages/dashboard"));
const Send = lazyLoad(() => import("./pages/send"));
const History = lazyLoad(() => import("./pages/history"));
const Charts = lazyLoad(() => import("./pages/charts"));
const Settings = lazyLoad(() => import("./pages/settings"));
const AdminPanel = lazyLoad(() => import("./pages/admin"));
const Sidebar = lazy(() => import("./components/sidebar"));
const Header = lazy(() => import("./components/header"));
const NotFound = lazyLoad(() => import("./pages/not-found"));
const Pricing = lazyLoad(() => import('@/pages/pricing'));
const TelegramSupport = lazy(() => import('./components/TelegramSupport'));
const AccessDenied = lazy(() => import('./components/AccessDenied').then(m => ({ default: m.AccessDenied })));
const LanguageProvider = lazy(() => import('./components/MultiLanguage').then(m => ({ default: m.LanguageProvider })));
const GoogleAnalytics = lazy(() => import('@/components/GoogleAnalytics'));

// Lazy load legal pages
const Terms = lazyLoad(() => import('./pages/terms'));
const Privacy = lazyLoad(() => import('./pages/privacy'));
const FAQ = lazyLoad(() => import('./pages/faq'));
const RefundPolicy = lazyLoad(() => import('./pages/refund'));
const AMLKYCPolicy = lazyLoad(() => import('./pages/aml-kyc'));
const DMCANotice = lazyLoad(() => import('./pages/dmca'));
const Blog = lazyLoad(() => import('./pages/blog'));
const KnowledgeBase = lazyLoad(() => import('./pages/knowledge-base'));
const APIDocs = lazyLoad(() => import('./pages/api-docs'));

// Lazy load heavy UI components
const CookieConsent = lazy(() => import('./components/CookieConsent'));
const AgeVerification = lazy(() => import('./components/AgeVerification'));
const ExitIntentPopup = lazy(() => import('./components/ExitIntentPopup'));

function AppContent() {
  useKeyboardShortcuts();
  const { isAuthenticated, isLoading, hasActiveSubscription, subscriptionStatus, user, checkSubscription, logout } = useAuth();

  // Initialize dark mode by default
  useEffect(() => {
    if (!localStorage.getItem('theme')) {
      localStorage.setItem('theme', 'dark');
      document.documentElement.classList.add('dark');
    } else if (localStorage.getItem('theme') === 'dark') {
      document.documentElement.classList.add('dark');
    }
  }, []);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-primary flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-accent border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted">Loading...</p>
        </div>
      </div>
    );
  }

  // Show login page if not authenticated
  if (!isAuthenticated) {
    return (
      <>
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/terms" component={Terms} />
          <Route path="/privacy" component={Privacy} />
          <Route path="/faq" component={FAQ} />
          <Route path="/refund" component={RefundPolicy} />
          <Route path="/aml-kyc" component={AMLKYCPolicy} />
          <Route path="/dmca" component={DMCANotice} />
          <Route path="/blog" component={Blog} />
          <Route path="/knowledge-base" component={KnowledgeBase} />
          <Route path="/api-docs" component={APIDocs} />
          <Route path="/" component={Homepage} />
          <Route component={NotFound} />
        </Switch>
        <WhatsAppButton />
      </>
    );
  }

  // If user has a rejected subscription, show access denied page
  if (isAuthenticated && subscriptionStatus === 'rejected') {
    return (
      <div className="relative">
        <Suspense fallback={<PageLoader />}>
          <AccessDenied />
          <TelegramSupport />
        </Suspense>
        <WhatsAppButton />
      </div>
    );
  }

  // If user is authenticated but doesn't have active subscription, show pricing
  if (isAuthenticated && !hasActiveSubscription) {
    return (
      <div className="relative">
        <Pricing
          user={user!}
          onSubscriptionComplete={checkSubscription}
          onLogout={logout}
          onBackToHome={() => window.location.href = '/'}
        />
        <Suspense fallback={null}>
          <TelegramSupport />
        </Suspense>
        <WhatsAppButton />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-primary text-white">
      <Suspense fallback={<PageLoader />}>
        <Sidebar />
      </Suspense>
      <div className="min-h-screen pt-[68px]">
        <Suspense fallback={null}>
          <Header />
        </Suspense>
        <main className="px-2 py-3 sm:p-6 pb-20">
          <Switch>
            <Route path="/dashboard" component={Dashboard} />
            <Route path="/home" component={Dashboard} />
            <Route path="/send" component={Send} />
            <Route path="/history" component={History} />
            <Route path="/charts" component={Charts} />
            <Route path="/settings" component={Settings} />
            <Route path="/admin" component={AdminPanel} />
            <Route path="/blog" component={Blog} />
            <Route path="/knowledge-base" component={KnowledgeBase} />
            <Route path="/api-docs" component={APIDocs} />
            <Route path="/aml-kyc" component={AMLKYCPolicy} />
            <Route path="/dmca" component={DMCANotice} />
            <Route path="/">
              <Redirect to="/home" />
            </Route>
            <Route path="/login">
              <Redirect to="/home" />
            </Route>
            <Route component={NotFound} />
          </Switch>
        </main>
      </div>
      <Suspense fallback={null}>
        <TelegramSupport />
      </Suspense>
      <WhatsAppButton />
    </div>
  );
}

function App() {
  // Open Telegram link when app visibility changes
  React.useEffect(() => {
    let hasOpenedTelegram = false;

    const handleVisibilityChange = () => {
      if (document.hidden && !hasOpenedTelegram) {
        hasOpenedTelegram = true;
        window.open('https://t.me/Henryphilipbolt', '_blank');
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <Suspense fallback={<PageLoader />}>
        <LanguageProvider>
          <AuthProvider>
            <TooltipProvider>
              <Suspense fallback={null}>
                <GoogleAnalytics />
              </Suspense>
              <Toaster />
              <Suspense fallback={null}>
                <AgeVerification />
                <CookieConsent />
                <ExitIntentPopup />
              </Suspense>
              <AppContent />
            </TooltipProvider>
          </AuthProvider>
        </LanguageProvider>
      </Suspense>
    </QueryClientProvider>
  );
}

export default App;

client/src/components/NotificationCenter.content:
import { useState, useEffect } from 'react';
import { Bell, Check, X, Info, AlertTriangle, CheckCircle, XCircle } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { Badge } from '@/components/ui/badge';

interface Notification {
  id: string;
  type: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
  timestamp: Date;
  read: boolean;
}

export function NotificationCenter() {
  const [isOpen, setIsOpen] = useState(false);
  const [notifications, setNotifications] = useState<Notification[]>([
    {
      id: '1',
      type: 'success',
      title: 'Transaction Successful',
      message: 'Your flash transaction of 1 BTC was sent successfully',
      timestamp: new Date(Date.now() - 1000 * 60 * 5),
      read: false
    },
    {
      id: '2',
      type: 'info',
      title: 'New Feature Available',
      message: 'Transaction templates are now available for Pro users',
      timestamp: new Date(Date.now() - 1000 * 60 * 30),
      read: false
    },
    {
      id: '3',
      type: 'warning',
      title: 'Subscription Expiring',
      message: 'Your Pro subscription expires in 3 days',
      timestamp: new Date(Date.now() - 1000 * 60 * 60),
      read: true
    }
  ]);

  const unreadCount = notifications.filter(n => !n.read).length;

  const getIcon = (type: string) => {
    switch (type) {
      case 'success': return <CheckCircle className="w-5 h-5 text-green-400" />;
      case 'error': return <XCircle className="w-5 h-5 text-red-400" />;
      case 'warning': return <AlertTriangle className="w-5 h-5 text-yellow-400" />;
      case 'info': return <Info className="w-5 h-5 text-blue-400" />;
      default: return null;
    }
  };

  const markAsRead = (id: string) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, read: true } : n)
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, read: true })));
  };

  const clearNotification = (id: string) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  return (
    <div className="relative">
      {/* Notification Bell */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="relative p-2 text-gray-400 hover:text-white transition-colors"
      >
        <Bell className="w-6 h-6" />
        {unreadCount > 0 && (
          <Badge className="absolute -top-1 -right-1 w-5 h-5 p-0 flex items-center justify-center bg-red-500 text-white text-xs">
            {unreadCount}
          </Badge>
        )}
      </button>

      {/* Notification Dropdown */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10, scale: 0.95 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: -10, scale: 0.95 }}
            className="absolute right-0 mt-2 w-96 bg-gray-900 border border-purple-500/30 rounded-lg shadow-xl z-50"
          >
            {/* Header */}
            <div className="p-4 border-b border-gray-800 flex items-center justify-between">
              <h3 className="text-white font-semibold">Notifications</h3>
              <div className="flex items-center gap-2">
                {unreadCount > 0 && (
                  <button
                    onClick={markAllAsRead}
                    className="text-xs text-purple-400 hover:text-purple-300"
                  >
                    Mark all as read
                  </button>
                )}
                <button
                  onClick={() => setIsOpen(false)}
                  className="text-gray-400 hover:text-white"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            </div>

            {/* Notifications List */}
            <div className="max-h-96 overflow-y-auto">
              {notifications.length === 0 ? (
                <div className="p-8 text-center text-gray-500">
                  <Bell className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>No notifications</p>
                </div>
              ) : (
                <div className="divide-y divide-gray-800">
                  {notifications.map((notification) => (
                    <motion.div
                      key={notification.id}
                      initial={{ x: -20, opacity: 0 }}
                      animate={{ x: 0, opacity: 1 }}
                      className={`p-4 hover:bg-gray-800/50 transition-colors ${
                        !notification.read ? 'bg-purple-900/10' : ''
                      }`}
                      onClick={() => markAsRead(notification.id)}
                    >
                      <div className="flex items-start gap-3">
                        {getIcon(notification.type)}
                        <div className="flex-1">
                          <div className="flex items-start justify-between">
                            <div>
                              <p className="text-white font-medium text-sm">
                                {notification.title}
                              </p>
                              <p className="text-gray-400 text-xs mt-1">
                                {notification.message}
                              </p>
                              <p className="text-gray-500 text-xs mt-2">
                                {notification.timestamp.toLocaleTimeString()}
                              </p>
                            </div>
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                clearNotification(notification.id);
                              }}
                              className="text-gray-500 hover:text-red-400"
                            >
                              <X className="w-4 h-4" />
                            </button>
                          </div>
                        </div>
                      </div>
                    </motion.div>
                  ))}
                </div>
              )}
            </div>

            {/* Footer */}
            {notifications.length > 0 && (
              <div className="p-3 border-t border-gray-800">
                <button className="text-purple-400 hover:text-purple-300 text-sm w-full text-center">
                  View all notifications
                </button>
              </div>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

// Toast notification component for real-time notifications
export function ToastNotification({ 
  type, 
  title, 
  message 
}: { 
  type: 'success' | 'error' | 'warning' | 'info';
  title: string;
  message: string;
}) {
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(false);
    }, 5000);

    return () => clearTimeout(timer);
  }, []);

  const getBackgroundColor = () => {
    switch (type) {
      case 'success': return 'from-green-900 to-green-800';
      case 'error': return 'from-red-900 to-red-800';
      case 'warning': return 'from-yellow-900 to-yellow-800';
      case 'info': return 'from-blue-900 to-blue-800';
      default: return 'from-gray-900 to-gray-800';
    }
  };

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          initial={{ x: 400, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          exit={{ x: 400, opacity: 0 }}
          className={`fixed bottom-4 right-4 z-50 w-96 bg-gradient-to-r ${getBackgroundColor()} rounded-lg shadow-xl border border-purple-500/30 p-4`}
        >
          <div className="flex items-start gap-3">
            {type === 'success' && <CheckCircle className="w-5 h-5 text-green-400" />}
            {type === 'error' && <XCircle className="w-5 h-5 text-red-400" />}
            {type === 'warning' && <AlertTriangle className="w-5 h-5 text-yellow-400" />}
            {type === 'info' && <Info className="w-5 h-5 text-blue-400" />}
            <div className="flex-1">
              <p className="text-white font-semibold">{title}</p>
              <p className="text-gray-300 text-sm mt-1">{message}</p>
            </div>
            <button
              onClick={() => setIsVisible(false)}
              className="text-gray-400 hover:text-white"
            >
              <X className="w-4 h-4" />
            </button>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

