import { useState, useEffect } from 'react';
import { Clock, MapPin, Monitor, Shield, AlertTriangle } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

interface LoginRecord {
  id: string;
  timestamp: Date;
  ipAddress: string;
  location: string;
  device: string;
  browser: string;
  status: 'success' | 'failed' | 'suspicious';
}

export default function LoginHistory() {
  const [loginHistory, setLoginHistory] = useState<LoginRecord[]>([
    {
      id: '1',
      timestamp: new Date(Date.now() - 1000 * 60 * 5),
      ipAddress: '192.168.1.1',
      location: 'New York, USA',
      device: 'Windows PC',
      browser: 'Chrome 120',
      status: 'success'
    },
    {
      id: '2',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2),
      ipAddress: '10.0.0.1',
      location: 'London, UK',
      device: 'MacBook Pro',
      browser: 'Safari 17',
      status: 'success'
    },
    {
      id: '3',
      timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24),
      ipAddress: '172.16.0.1',
      location: 'Unknown',
      device: 'Unknown',
      browser: 'Unknown',
      status: 'failed'
    }
  ]);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'success': return 'bg-green-500';
      case 'failed': return 'bg-red-500';
      case 'suspicious': return 'bg-yellow-500';
      default: return 'bg-gray-500';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'success': return <Shield className="w-4 h-4" />;
      case 'failed': return <AlertTriangle className="w-4 h-4" />;
      case 'suspicious': return <AlertTriangle className="w-4 h-4" />;
