                  </div>
                  <div className="flex items-center gap-2 text-gray-400">
                    <span className="text-xs">IP: {login.ipAddress}</span>
                  </div>
                </div>
              </div>

              {login.status === 'suspicious' && (
                <button className="text-red-400 hover:text-red-300 text-sm">
                  Report
