@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(0, 0%, 9%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(210, 40%, 20%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(45, 100%, 51%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.75rem;
  --success: hsl(149, 100%, 50%);
  --warning: hsl(43, 100%, 50%);
  --danger: hsl(348, 83%, 58%);
  --crypto-glow: 0 0 30px hsla(45, 100%, 51%, 0.4);

  /* Enterprise Colors */
  --enterprise-dark: hsl(220, 26%, 11%);
  --enterprise-darker: hsl(220, 41%, 7%);
  --enterprise-gold: hsl(45, 100%, 51%);
  --enterprise-gold-dim: hsl(45, 80%, 40%);
  --enterprise-border: hsl(220, 20%, 20%);
  --enterprise-gradient: linear-gradient(135deg, hsl(220, 41%, 7%) 0%, hsl(220, 26%, 11%) 100%);
}

.dark {
  --background: hsl(220, 26%, 11%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(220, 20%, 15%);
  --muted-foreground: hsl(220, 10%, 65%);
  --popover: hsl(220, 41%, 7%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(220, 41%, 7%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(220, 20%, 20%);
  --input: hsl(220, 20%, 15%);
  --primary: hsl(220, 41%, 7%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(220, 26%, 14%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(45, 100%, 51%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(45, 100%, 51%);
  --radius: 0.75rem;
  --success: hsl(149, 100%, 50%);
  --warning: hsl(43, 100%, 50%);
  --danger: hsl(348, 83%, 58%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-primary text-foreground;
    font-family: 'SF Pro Display', 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
    letter-spacing: -0.01em;
    background: var(--enterprise-gradient);
    min-height: 100vh;
  }

  /* Enterprise Typography */
  h1 { @apply text-4xl font-bold tracking-tight; }
  h2 { @apply text-3xl font-semibold tracking-tight; }
  h3 { @apply text-2xl font-semibold tracking-tight; }
  h4 { @apply text-xl font-medium tracking-normal; }
  h5 { @apply text-lg font-medium tracking-normal; }
  h6 { @apply text-base font-medium tracking-normal; }
}

@layer components {
  /* Enterprise Glass Card */
  .glass-card {
    background: linear-gradient(135deg, rgba(30, 35, 48, 0.95) 0%, rgba(20, 24, 35, 0.95) 100%);
    backdrop-filter: blur(20px) saturate(150%);
    border: 1px solid rgba(255, 215, 0, 0.1);
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.37),
      inset 0 1px 0 rgba(255, 215, 0, 0.05),
      inset 0 -1px 0 rgba(0, 0, 0, 0.25);
    position: relative;
  }

  .glass-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 1px;
    background: linear-gradient(90deg, 
      transparent 0%, 
      rgba(255, 215, 0, 0.3) 50%, 
      transparent 100%);
    opacity: 0.5;
  }

  /* Enterprise Header */
  .enterprise-header {
    background: linear-gradient(135deg, rgba(20, 24, 35, 0.98) 0%, rgba(15, 18, 27, 0.98) 100%);
    backdrop-filter: blur(20px) saturate(180%);
    border-bottom: 1px solid rgba(255, 215, 0, 0.15);
    box-shadow: 
      0 4px 24px rgba(0, 0, 0, 0.4),
      inset 0 1px 0 rgba(255, 215, 0, 0.03);
  }

  /* Enterprise Sidebar */
  .enterprise-sidebar {
    background: linear-gradient(180deg, rgba(15, 18, 27, 0.98) 0%, rgba(10, 12, 20, 0.98) 100%);
    backdrop-filter: blur(24px) saturate(180%);
    border-right: 1px solid rgba(255, 215, 0, 0.08);
    box-shadow: 
      4px 0 24px rgba(0, 0, 0, 0.3),
      inset -1px 0 0 rgba(255, 215, 0, 0.05);
  }

  /* Enterprise Nav Item */
  .enterprise-nav-item {
    position: relative;
    overflow: hidden;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    border-radius: 0.625rem;
  }

  .enterprise-nav-item::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 3px;
    height: 100%;
    background: linear-gradient(180deg, transparent, var(--enterprise-gold), transparent);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .enterprise-nav-item:hover::before,
  .enterprise-nav-item.active::before {
    opacity: 1;
  }

  .enterprise-nav-item:hover {
    background: linear-gradient(90deg, rgba(255, 215, 0, 0.08) 0%, rgba(255, 215, 0, 0.02) 100%);
    transform: translateX(2px);
  }

  .enterprise-nav-item.active {
    background: linear-gradient(90deg, rgba(255, 215, 0, 0.15) 0%, rgba(255, 215, 0, 0.05) 100%);
    box-shadow: 
      0 2px 8px rgba(255, 215, 0, 0.1),
      inset 0 1px 0 rgba(255, 215, 0, 0.1);
  }

  /* Enterprise Button */
  .enterprise-button {
    background: linear-gradient(135deg, var(--enterprise-gold) 0%, hsl(45, 90%, 45%) 100%);
    color: #000;
    font-weight: 600;
    letter-spacing: 0.025em;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 
      0 4px 14px rgba(255, 215, 0, 0.25),
      inset 0 1px 0 rgba(255, 255, 255, 0.3);
  }

  .enterprise-button:hover {
    transform: translateY(-1px);
    box-shadow: 
      0 6px 20px rgba(255, 215, 0, 0.35),
      inset 0 1px 0 rgba(255, 255, 255, 0.3);
  }

  .enterprise-button:active {
    transform: translateY(0);
  }

  /* Enterprise Card */
  .enterprise-card {
    background: linear-gradient(135deg, rgba(25, 30, 42, 0.95) 0%, rgba(18, 22, 32, 0.95) 100%);
    border: 1px solid rgba(255, 215, 0, 0.08);
    border-radius: 0.875rem;
    box-shadow: 
      0 10px 40px rgba(0, 0, 0, 0.3),
      inset 0 1px 0 rgba(255, 215, 0, 0.03);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .enterprise-card:hover {
    transform: translateY(-2px);
    box-shadow: 
      0 14px 48px rgba(0, 0, 0, 0.35),
      0 0 0 1px rgba(255, 215, 0, 0.1),
      inset 0 1px 0 rgba(255, 215, 0, 0.05);
  }

  /* Gold Accent Text */
  .text-gold {
    color: var(--enterprise-gold);
    text-shadow: 0 0 20px rgba(255, 215, 0, 0.3);
  }

  .text-gold-gradient {
    background: linear-gradient(135deg, var(--enterprise-gold) 0%, hsl(45, 100%, 65%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Enterprise Glow Effects */
  .gold-glow {
    box-shadow: 0 0 30px rgba(255, 215, 0, 0.3);
  }

  .gold-text-glow {
    text-shadow: 0 0 20px rgba(255, 215, 0, 0.4);
  }

  /* Enterprise Animations */
  @keyframes goldPulse {
    0%, 100% { 
      opacity: 1; 
      box-shadow: 0 0 20px rgba(255, 215, 0, 0.3);
    }
    50% { 
      opacity: 0.8; 
      box-shadow: 0 0 30px rgba(255, 215, 0, 0.5);
    }
  }

  @keyframes shimmer {
    0% { background-position: -1000px 0; }
    100% { background-position: 1000px 0; }
  }

  .gold-pulse {
    animation: goldPulse 3s ease-in-out infinite;
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      transparent 0%,
      rgba(255, 215, 0, 0.05) 50%,
      transparent 100%
    );
    background-size: 1000px 100%;
    animation: shimmer 8s linear infinite;
  }

  /* Slow Bounce Animation for WhatsApp Button */
  @keyframes bounce-slow {
    0%, 100% {
      transform: translateY(0);
    }
    50% {
      transform: translateY(-10px);
    }
  }

  .animate-bounce-slow {
    animation: bounce-slow 3s ease-in-out infinite;
  }

  /* Professional Status Indicators */
  .status-online {
    width: 8px;
    height: 8px;
    background: radial-gradient(circle, #10b981 0%, #059669 100%);
    border-radius: 50%;
    box-shadow: 0 0 10px rgba(16, 185, 129, 0.5);
    animation: statusPulse 2s ease-in-out infinite;
  }

  @keyframes statusPulse {
    0%, 100% { 
      opacity: 1;
      transform: scale(1);
    }
    50% { 
      opacity: 0.7;
      transform: scale(1.1);
    }
  }

  /* Enterprise Forms */
  .enterprise-input {
    background: rgba(15, 18, 27, 0.6);
    border: 1px solid rgba(255, 215, 0, 0.1);
    transition: all 0.3s ease;
  }

  .enterprise-input:focus {
    border-color: rgba(255, 215, 0, 0.3);
    box-shadow: 0 0 0 3px rgba(255, 215, 0, 0.05);
    background: rgba(15, 18, 27, 0.8);
  }

  /* Premium Scrollbar */
  ::-webkit-scrollbar {
    width: 10px;
    height: 10px;
  }

  ::-webkit-scrollbar-track {
    background: rgba(15, 18, 27, 0.5);
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(180deg, rgba(255, 215, 0, 0.2), rgba(255, 215, 0, 0.1));
    border-radius: 5px;
    border: 1px solid rgba(255, 215, 0, 0.1);
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(180deg, rgba(255, 215, 0, 0.3), rgba(255, 215, 0, 0.2));
  }

  /* Legacy Support */
  .gradient-bg {
    background: var(--enterprise-gradient);
  }

  .crypto-glow {
    box-shadow: var(--crypto-glow);
  }

  .balance-glow {
    text-shadow: 0 0 15px hsla(149, 100%, 50%, 0.6);
  }

  .sidebar-item:hover {
    background: linear-gradient(90deg, rgba(255, 215, 0, 0.08) 0%, transparent 100%);
  }

  .transaction-card {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .transaction-card:hover {
    transform: translateY(-3px);
    box-shadow: 0 12px 35px rgba(0, 0, 0, 0.4);
  }

  .pulse-animation {
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  /* Mobile Optimizations */
  @media (max-width: 768px) {
    .glass-card {
      backdrop-filter: blur(15px);
      border-radius: 0.625rem;
    }

    .enterprise-card {
      border-radius: 0.625rem;
    }

    .transaction-card:hover {
      transform: none;
    }
  }
}

